mapper.select:
select * 
	from (select rownum, B.*
	from (select * from board_view order by reg_date desc) B)
where rownum between #{start} and #{end}

select * 
	from (select rownum, B.*
	from (select * from board order by reg_date desc) B)
where user_id = #{writer.userId} 
	and rownum between #{start} and #{end}
